mapper.select:

select * from (
	select rownum rnum, TT.* from (
		select * from book
	) TT where rownum <= #{endNo} 
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from book where avg > 8 order by DBMS_RANDOM.RANDOM
	) TT where rownum <= 10
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from book order by avg desc
	) TT where rownum <= 10
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from book where TO_DATE(pDate) > sysdate - 90 order by pdate desc
	) TT where rownum <= #{endNo} 
) where rnum >= #{startNo}


select * from (
	select rownum rnum, TT.* from (
		select * from book where category = #{list}
	) TT where rownum <= #{endNo}
) where rnum >= #{startNo}

select * from (
	select rownum rnum, TT.* from (
		select * from book where

				title like '%${item}%'


				author like '%${item}%'


				title like '%${item}%' or author like '%${item}%'


	) TT where rownum <= #{endNo} 
) where rnum >= #{startNo}
